<?phpinclude "db.php";include "backend.php";include "create-index.ads-data.php";$page['content']=create_ads_list($ads);$page['save']='advertisements.html';$page['title']='Mazanex اگهی های روی';include "page.php";function create_ads_list($ads){	$out='<table class="ad">';	$out.='<tr><th colspan="4">آگهی های زیر بر روی سایت نمایش داده می شوند. برای کسب اطلاعات بیشتر در رابطه با نحوه درج آگهی به صفحه " <a href="/ads.html">آگهی</a> " مراجعه نمایید.</th></tr><tr>';	$counter=0;	foreach ($ads as $ad){		if ($avoid_duplicate[$ad['img']]) continue;		$avoid_duplicate[$ad['img']]=true;		if ($counter==4){			$counter=0;			$out.='</tr><tr>';		}		$out.='<td><a href="'.$ad['url'].'" target="_blank"><img src="'.$ad['img'].'" /></a></td>';		$counter++;	}	return $out.'</tr></table>';}